using System;
using System.Collections.Generic;

namespace Alg_03.Core
{
    public abstract class AbstractSort<T>
        where T : IComparable
    {
        public enum SortOrder
        {
            Ascending = 1,
            Descending = -1
        }

        public int AssignmentCount { get; protected set; }
        public int CompareCount { get; private set; }

        public IList<T> List { get; private set; } = new List<T>();

        public SortOrder Order { get; set; } = SortOrder.Ascending;

        protected int Compare(T a, T b)
        {
            CompareCount++;
            return (int) Order * a.CompareTo(b);
        }

        public virtual void Sort(IList<T> list)
        {
            List = list;
            AssignmentCount = 0;
            CompareCount = 0;
        }
    }
}

using System;
using System.Collections.Generic;

namespace Alg_03.Core
{
    public class InclusionSort<T> : AbstractSort<T>
        where T : IComparable
    {
        public override void Sort(IList<T> list)
        {
            base.Sort(list);
            for (var i = 1; i < List.Count; i++)
            {
                var value = List[i];
                var j = i;
                while (j > 0 && Compare(List[j - 1], value) > 0)
                {
                    AssignmentCount++;
                    List[j] = List[j - 1];
                    j--;
                }

                AssignmentCount++;
                List[j] = value;
            }
        }
    }
}

using System;
using System.Collections.Generic;

namespace Alg_03.Core
{
    public class SelectionSort<T> : AbstractSort<T>
        where T : IComparable
    {
        public override void Sort(IList<T> list)
        {
            base.Sort(list);
            for (var i = 0; i < List.Count - 1; i++)
            {
                var min = i;

                for (var j = i + 1; j < List.Count; j++)
                {
                    if (Compare(List[j], List[min]) < 0)
                    {
                        min = j;
                    }
                }

                AssignmentCount += 2;
                var temp = List[i];
                List[i] = List[min];
                List[min] = temp;
            }
        }
    }
}

