        [Theory]
        public void ListSortTest(List<T> list)
        {
            Sort.Sort(list);
            Assert.That(list.OrderByDescending(p => p).SequenceEqual(list), Is.False);
            Assert.That(list.OrderBy(p => p).SequenceEqual(list), Is.True);
        }

