            Sort.Order = AbstractSort<T>.SortOrder.Descending;
            Sort.Sort(list);
            Assert.That(list.OrderByDescending(p => p).SequenceEqual(list), Is.True);
            Assert.That(list.OrderBy(p => p).SequenceEqual(list), Is.False);
        }
